import React,{useState} from "react";
import {useParams} from "react-router-dom";

const Transcript=()=>{
    const {trans_id}= useParams();
    const [ttext,setText]= useState(null);
   // let text="";
   const importAll=(r)=> {
    let images = {};
    r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
    return images;
  }
  
  const images = importAll(require.context('../components/images/keys', false, /\.(png|jpe?g|svg)$/));
React.useEffect(() => {
    fetch(`/transcript/${trans_id}`)
      .then((res) => res.json())
      .then((data) => {
          //console.log(text);
          setText(data.transcript);
      });
  }, []);
  console.log(ttext);
  const arr=[]
  const logImg=()=>{
      
      if(ttext!=null){
   ttext.toUpperCase().split("").forEach(ch=>{
         arr.push(ch>='A' && ch<='Z'?(<img src={images[`${ch}.jpg`].default}/>):(" "))
      })
      console.log(arr);
    }
    
  }
  return (
      <div>
            
           
               
                {ttext!=null?{logImg}:" LOADING...."
                }
                
           
                
      </div>
  )
}
export default Transcript
