import React from "react";
import './css/question.css';
import  CircularProgressbar  from "../components/circularProgress";
class Question extends React.Component{
    constructor(props){
        super();
        this.state={
            marked:"",
            A_class:"",
            B_class:"",
            C_class:"",
            D_class:""
        };
        this.answer=props.answer;
    }
    
    check=()=>{
      
        if(this.state.marked!=this.answer){
            if(this.answer=='D'){
                this.setState({A_class:"wrong" , B_class:"wrong",C_class:"wrong",
                D_class: "correct"})
            }
            if(this.answer=='C'){
                this.setState({A_class:"wrong" , B_class:"wrong",C_class:"correct",
                D_class: "wrong"})
            }
            
            if(this.answer=='B'){
                this.setState({A_class:"wrong" , B_class:"correct",C_class:"wrong",
                D_class: "wrong"})
            }
            if(this.answer=='A'){
                this.setState({A_class:"correct" , B_class:"wrong",C_class:"wrong",
                D_class: "wrong"})
            }
        }else{
                if(this.answer=='D'){
                    this.setState({
                    D_class: "correct"})
            }
            if(this.answer=='C'){
                this.setState({
                C_class: "correct"})
        }
        if(this.answer=='B'){
            this.setState({
            B_class: "correct"})
    }
    if(this.answer=='A'){
        this.setState({
        A_class: "correct"})
}
           
        }
    }
    handle=(event)=>{
       // console.log(event.target.value)
        this.setState({marked:event.target.value})
    }
    render(){
    return(
        <div>
               <CircularProgressbar value={this.props.num*10}/>
            <div className="que-container">
                <p>Q. {this.props.question}</p>
            </div>
            <div className="row options">
                <div className="col-lg-6">
                    <div className={"option  "+ this.state.A_class}>
                    <input type= "radio" name="q1" value="A"onChange={this.handle} />
                    {
                        (this.props.a.type=='text')?
                            <p>{this.props.a.data}</p>:
                            <img src={this.props.a.data}/>
                        
                    }
                    </div>
                 
                </div>
                <div className="col-lg-6">
                    <div className={"option  "+ this.state.B_class}>
                <input type= "radio" name="q1" value="B" onChange={this.handle}/>
                {
                        (this.props.b.type=='text')?
                            <p>{this.props.b.data}</p>:
                            <img src={this.props.b.data}/>
                        
                    }
                </div>
                </div>
            </div>
            <div className="row options">
                <div className="col-lg-6">
                <div  className={"option  "+ this.state.C_class}>
                <input type= "radio" name="q1" value="C" onChange={this.handle} />
                {
                        (this.props.c.type=='text')?
                            <p>{this.props.c.data}</p>:
                            <img src={this.props.c.data}/>
                        
                    }
                </div>

                </div>
                <div className="col-lg-6">
                <div className={"option  "+ this.state.D_class}>
                <input type= "radio" name="q1" value="D" onChange={this.handle} />
                {
                        (this.props.d.type=='text')?
                            <p>{this.props.d.data}</p>:
                            <img src={this.props.d.data}/>
                        
                    }
                </div>
                </div>
                </div>
            <button className="check btn btn-success" onClick={this.check}>Check Answer</button>
            
        </div>
    
    )
}
}
export default Question
