import React from "react";

class Translate extends React.Component{
    constructor(){
        super();
        this.state={
            text:""
        }
    }
  
    addText=(event)=>{
     //   console.log(event.target.value);
        this.setState({text:event.target.value});
    }
    translate=()=>{
        let a= this.state.text.toUpperCase().split("");
        console.log(a);
        a.map((letter)=>{
            {(letter>='A' && letter<='Z')?<img src={images[{letter}].default}/>:""}
        })
    }
    render(){
        function importAll(r) {
            let images = {};
            r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
            return images;
          }
          
          const images = importAll(require.context('../components/images/keys', false, /\.(png|jpe?g|svg)$/));
          console.log(images);
        return(
            <div>
                <div>
                    <input type="text" onChange={this.addText}/>
                </div>
                <button onClick={this.translate}>Translate</button>
            </div>
        );
    }
}
export default Translate;
