import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import Practice from './pages/practice';

class App extends Component {
  render() {

    this.state= {
      letter:0
    }

    const App = () => (

      <div>
        <Switch>
          <Route exact path='/practice'   render={() => (
            <Practice
            //pass the props through the route
              letter={this.state.letter}
            />
          )}
          exact={true}/>

<Route exact path='/'   render={() => (
           <button onClick={this.setState({letter:3})}>Change Letter</button>
          )}
          exact={true}/>
        </Switch>
      </div>
    )
    return (
      <Switch>
        {console.log(letter)}
        <App/>
      </Switch>
    );
  }
}

export default App;
