import React, { useRef, useState, useEffect } from "react";
import swal from "sweetalert";

import * as tf from "@tensorflow/tfjs";
import * as handpose from "@tensorflow-models/handpose";
import Webcam from "react-webcam";

import { drawHand } from "../utilities";

import * as fp from "fingerpose";
import Handsigns from './handsigns'
import {Signimage,Signpass} from './handimage'

function App() {
  const webcamRef = useRef(null);
  const canvasRef = useRef(null);
  const [emoji, setEmoji] = useState(null);
  let letters=['A','B','C']
  const images = { A: Signimage.A, 
    B: Signimage.B, 
    C: Signimage.C,
    D: Signimage.D,
    E:Signimage.E,
    F:Signimage.F,
    G:Signimage.G,
    H:Signimage.H,
    I:Signimage.I,
    J:Signimage.J,
    K:Signimage.K,
    L:Signimage.L,
    M: Signimage.M, 
    N: Signimage.N,
    O: Signimage.O,
    P:Signimage.P,
    Q:Signimage.Q,
    R:Signimage.R,
    S:Signimage.S,
    T:Signimage.T,
    U:Signimage.U,
    V:Signimage.V,
    W:Signimage.W,
    X:Signimage.X,
    Y:Signimage.Y,
    Z:Signimage.Z,
  };
  const signs={
    A: Signpass.A,
    B: Signpass.B,
  }
 
  let currentSign=0;
 
  const runHandpose = async () => {
    const net = await handpose.load();
    
    console.log("signs:",Signpass[0]);
    console.log("Handpose model loaded.");
    //  Loop and detect hands
    setInterval(() => {
      detect(net);
    }, 10);
  };
  const random=()=>{
    
      const j = Math.floor(Math.random() * (Signpass.length + 1));
      return j;
  
}


  const detect = async (net) => {
    // Check data is available
    if (
      typeof webcamRef.current !== "undefined" &&
      webcamRef.current !== null &&
      webcamRef.current.video.readyState === 4
    ) {
      // Get Video Properties
      const video = webcamRef.current.video;
      const videoWidth = webcamRef.current.video.videoWidth;
      const videoHeight = webcamRef.current.video.videoHeight;

      // Set video width
      webcamRef.current.video.width = videoWidth;
      webcamRef.current.video.height = videoHeight;

      // Set canvas height and width
      canvasRef.current.width = videoWidth;
      canvasRef.current.height = videoHeight;

      // Make Detections
      const hand = await net.estimateHands(video);
      // console.log(hand);

      if (hand.length > 0) {
        const GE = new fp.GestureEstimator([
         
          Handsigns.aSign,
          Handsigns.bSign,Handsigns.cSign, Handsigns.dSign, Handsigns.eSign, Handsigns.fSign, Handsigns.gSign,
          Handsigns.hSign, Handsigns.iSign, Handsigns.jSign, Handsigns.kSign, Handsigns.lSign, Handsigns.mSign, Handsigns.nSign,
          Handsigns.oSign, Handsigns.pSign, Handsigns.qSign, Handsigns.rSign, Handsigns.sSign, Handsigns.tSign, Handsigns.uSign,
          Handsigns.vSign, Handsigns.wSign, Handsigns.xSign, Handsigns.ySign, Handsigns.zSign
        ]);
        const gesture = await GE.estimate(hand[0].landmarks, 4);
        if (gesture.gestures !== undefined && gesture.gestures.length > 0) {
        

          const confidence = gesture.gestures.map(
            (prediction) => prediction.confidence
          );
          const maxConfidence = confidence.indexOf(
            Math.max.apply(null, confidence)
          );
           //console.log(gesture.gestures[maxConfidence].name);
           console.log(Signpass[currentSign].alt);
           if (Signpass[currentSign].alt === gesture.gestures[maxConfidence].name) {
            swal(
              <div>
                <h1>Hello world!</h1>
                <p>
                  This is now rendered with JSX!
                </p>
              </div>
            )
              currentSign++;
           
          }
       
          setEmoji(gesture.gestures[maxConfidence].name);
       //   console.log(emoji);
        }
      }
      const ctx = canvasRef.current.getContext("2d");
      drawHand(hand, ctx);
    }
  };

  useEffect(()=>{runHandpose()},[]);

  return (
    <div className="App">
      <header className="App-header">
     
        <Webcam
          ref={webcamRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
         
            right: 0,
            textAlign: "center",
            zindex: 9,
            width: 600,
            height: 480,
          }}
        />

        <canvas
          ref={canvasRef}
          style={{
            position: "absolute",
            marginLeft: "auto",
            marginRight: "auto",
      
            right: 0,
            textAlign: "center",
            zindex: 9,
           
          }}
        />
        {/* NEW STUFF */}
        {emoji !== null ? (
          <img
            src={images[emoji]}
            style={{
              position: "absolute",
              marginLeft: "auto",
              marginRight: "auto",
           
              bottom: 500,
              right: 0,
              textAlign: "center",
              height: 100,
            }}
          />
        ) : (
          ""
        )}
         <img
            src={images['A']}
            style={{
              position: "absolute",
              marginLeft: "auto",
              marginRight: "auto",
              width:500,
              
            left: 0,
              textAlign: "center",
              height: 480,
              zIndex:9
            }}
          />

       
      </header>
    </div>
  );
}

export default App;
