import React from "react";
import './css/question.css';
import A from './images/keys/A.jpg'
import Z from './images/keys/Z.jpg'
import R from './images/keys/R.jpg'
import T from './images/keys/T.jpg'
class Question extends React.Component{
    constructor(){
        super();
        this.state={
            marked:"",
            A_class:"",
            B_class:"",
            C_class:"",
            D_class:""
        }
    }
    answer="D";
    check=()=>{
      
        if(this.state.marked==this.answer){
            if(this.answer=='D'){
                this.setState({D_class: "correct"})
            }
           
        }
    }
    handle=(event)=>{
       // console.log(event.target.value)
        this.setState({marked:event.target.value})
    }
    render(){
    return(
        <div>
            <div className="que-container">
                <p>Q. Which of the following represents the letter A in ASL?</p>
            </div>
            <div className="row options">
                <div className="col-lg-6">
                    <div className="option">
                    <input type= "radio" name="q1" value="A"onChange={this.handle} className={this.state.A_class}/>
                    <img src={R}></img>
                    </div>
                 
                </div>
                <div className="col-lg-6">
                    <div className="option"  className={this.state.B_class} >
                <input type= "radio" name="q1" value="B" onChange={this.handle}/>
                <img src={T}></img>
                </div>
                </div>
            </div>
            <div className="row options">
                <div className="col-lg-6">
                <div  className="option" className={this.state.C_class}>
                <input type= "radio" name="q1" value="C" onChange={this.handle} />
                <img src={Z}></img>
                </div>

                </div>
                <div className="col-lg-6">
                <div className="option" className={this.state.D_class}>
                <input type= "radio" name="q1" value="D" onChange={this.handle} />
                    <img src={A}></img>
                </div>
                </div>
                </div>
            <button className="check btn btn-success" onClick={this.check}>Check Answer</button>
        </div>
    
    )
}
}
export default Question
