import React, { Component } from 'react';
import { Route, Switch } from 'react-router-dom';
import Practice from './pages/practice';

class App extends Component {
  render() {
    let letter = 0;
    const changeLetter=()=>{
      letter= 2;
      console.log(letter);
    }
    const App = () => (

      <div>
        <Switch>
          <Route exact path='/practice'   render={() => (
            <Practice
            //pass the props through the route
              letter={letter}
            />
          )}
          exact={true}/>

<Route exact path='/'   render={() => (
           <button onClick={changeLetter}><a href="/practice">Change Letter</a></button>
          )}
          exact={true}/>
        </Switch>
      </div>
    )
    return (
      <Switch>

        <App/>
      </Switch>
    );
  }
}

export default App;
